        return new Source("{% extends 'base.html.twig' %}


{% block body %}
    <div class=\"container\">
        
        <h1 class=\"mt-4\">Cr√©ation d'une recette</h1>
        
        {{ form_start(form) }}
            
        <div class=\"form-group\">
            {{ form_label(form.name) }}
            {{ form_widget(form.name)}}
            <div class=\"form-error\">{{ form_errors(form.name) }}</div>
        </div>
        <div class=\"form-group\">
            {{ form_label(form.time) }}
            {{ form_widget(form.time)}}
            <div class=\"form-error\">{{ form_errors(form.time) }}</div>
        </div>
        <div class=\"form-group\">
            {{ form_label(form.nbPeople) }}
            {{ form_widget(form.nbPeople)}}
            <div class=\"form-error\">{{ form_errors(form.nbPeople) }}</div>
        </div>
        <div class=\"form-group\">
            {{ form_label(form.difficulty) }}
            {{ form_widget(form.difficulty)}}
            <div class=\"form-error\">{{ form_errors(form.difficulty) }}</div>
        </div>
        <div class=\"form-group\">
            {{ form_label(form.description) }}
            {{ form_widget(form.description)}}
            <div class=\"form-error\">{{ form_errors(form.description) }}</div>
        </div>
        <div class=\"form-group\">
            {{ form_label(form.price) }}
            {{ form_widget(form.price)}}
            <div class=\"form_error\">{{ form_errors(form.price) }}</div> 
        </div>
        <div class=\"form-check mt-4\">
            {{ form_widget(form.isFavorite)}}
            {{ form_label(form.isFavorite) }}
            <div class=\"form-error\">{{ form_errors(form.isFavorite) }}</div>
        </div>
        <div class=\"form-group\">
            {{ form_label(form.ingredients) }}
            {{ form_widget(form.ingredients)}}
            <div class=\"form-error\">{{ form_errors(form.ingredients) }}</div>
        </div>
        
            {{ form_row(form.submit, { 'label': 'Cr√©ation de la recette' })}}
        
        {{ form_end(form) }}     
    </div>
{% endblock %}", "pages/recipe/new.html.twig", "/var/www/html/symrecipe/templates/pages/recipe/new.html.twig");
